import React, { Component } from "react";
import "../../CSS/NewsFullView.css";

class NewsFullView extends Component {
  render() {
    return (
      // <div className="text-justify text-center ">
      //   <div id="align-top"></div>
      //   <div className="MainNewsFullView ">
      //     <h1 className="titleFullView">{this.props.titleFullView}</h1>
      //     <div
      //       className="fb-like"
      //       data-href={"http://localhost:3000/" + this.props.partURL_plugin_fb}
      //       data-layout="button_count"
      //       data-action="like"
      //       data-size="large"
      //       data-share="true"
      //     ></div>
      //     <img className="imgFullView" src={this.props.imgFullView}></img>
      //     <div className="contentFullView">{this.props.contentFullView}</div>
      //   </div>
      //   <div className="comment">
      //     <h4>Ý kiến của bạn</h4>
      //     <div
      //       className="fb-comments"
      //       data-href={"http://localhost:3000/" + this.props.partURL_plugin_fb}
      //       data-numposts="10"
      //       data-width="900"
      //     ></div>
      //   </div>
      // </div>
      <div className="container-fluid text-justify px-3">
        <div id="align-top"></div>
        <div className="borderNews px-3 py-4">
          <div className="row">
            <div className="col-lg-12 col-sm-12 mb-12">
              <h1 className="mt-2 text-center">{this.props.titleFullView}</h1>
            </div>
            <div className="row mb-3 mx-1">
              <div className="col-lg-12 col-sm-12 mb-12">
                <div
                  className="fb-like"
                  data-href={
                    "http://localhost:3000/" + this.props.partURL_plugin_fb
                  }
                  data-layout="button_count"
                  data-action="like"
                  data-size="large"
                  data-share="true"
                ></div>
              </div>
            </div>
          </div>

          <div className="row ">
            <div className="col-lg-12 col-sm-12 mb-12  ">
              <img
                className="imgFullView text-justify text-center"
                src={this.props.imgFullView}
              ></img>
              <p className="mt-4">{this.props.contentFullView}</p>
            </div>
          </div>
        </div>

        <div className="row SwitchNews mt-3 mx-4">
          <div className="col-lg-12 col-sm-12 mb-12 ">
            <a href="#" className="btn btn-success py-3 pt-4 btnSwitch  btnPrevious ">
              <h5>
                <i className="far fa-arrow-alt-circle-left"></i> Previous
              </h5>
            </a>

            <a href="#" className="btn btn-success  py-3 px-4 pt-4 btnSwitch btnNext">
              <h5>
                Next <i className="far fa-arrow-alt-circle-right"></i>
              </h5>
            </a>
          </div>
        </div>
        <div className="row text-justify text-center mt-3">
          <div className="col-lg-12 col-sm-12 mb-12">
              <h4>Ý kiến của bạn</h4>
              <div
                className="fb-comments"
                data-href={
                  "http://localhost:3000/" + this.props.partURL_plugin_fb
                }
                data-numposts="10"
                data-width="900"
              ></div>
          </div>
        </div>
      </div>
    );
  }
}

export default NewsFullView;
