import React, { Component } from "react";
import { HashRouter as Router, Route } from "react-router-dom";
import Home from "./Home/Home.js";
import Search from "./Search.js";
import Contact from "./Contact/Contact.js";
import About from "./About.js";
import data from "../SIMULATION_DATABASE/data_BlockNews.json";
import FVdata from "../SIMULATION_DATABASE/data_NewsFullView.json";
// import component xem full bảng tin
import NewsFullView from "./Home/news/newsFullView/NewsFullView.js";
import NewsFullView1 from "./Home/news/newsFullView/NewsFullView.js";
import NewsFullView2 from "./Home/news/newsFullView/NewsFullView1.js";
import NewsFullView3 from "./Home/news/newsFullView/NewsFullView2.js";
import NewsFullView4 from "./Home/news/newsFullView/NewsFullView3.js";
import NewsFullView5 from "./Home/news/newsFullView/NewsFullView4.js";

class RouterURL extends Component {
  render() {
    const index = [1, 2, 3, 4, 5, 6];

    return (
      <div>
        <Route exact path="/" component={Home} />
        <Route exact path="/home" component={Home} />
        <Route exact path="/search" component={Search} />
        <Route exact path="/contact" component={Contact} />
        <Route exact path="/about" component={About} />
        {/* chuyển đến trang xem full bảng tin */}

        {index.map((value) => {
          return (
            <Route
              exact
              path={"/" + data[value].linkTopic}
              render={(props) => (
                <NewsFullView
                  titleFullView={FVdata[value].title}
                  imgFullView={FVdata[value].image}
                  contentFullView={FVdata[value].content}
                  partURL_plugin_fb={data[0].linkTopic}
                />
              )}
            />
          );
        })}

        {/* <Route
          exact
          path={"/" + data[0].linkTopic}
          render={(props) => (
            <NewsFullView
              titleFullView={FVdata[0].title}
              imgFullView={FVdata[0].image}
              contentFullView={FVdata[0].content}
              partURL_plugin_fb={data[0].linkTopic}
            />
          )}
        /> */}
      </div>
    );
  }
}

export default RouterURL;
